 * @param  string  $field 
 * @return string         
 */
function get_category($id, $field = null){
    static $list;

    /* ID */
    if(empty($id) || !is_numeric($id)){
        return '';
    }

    /*  */
    if(empty($list)){
        $list = S('sys_category_list');
    }

    /*  */
    if(!isset($list[$id])){
        $cate = M('Category')->find($id);
        if(!$cate || 1 != $cate['status']){ //
            return '';
        }
        $list[$id] = $cate;
        S('sys_category_list', $list); //
    }
    return is_null($field) ? $list[$id] : $list[$id][$field];
}

